mapper.select:
SELECT id, user_id, item_id, quantity, created_at, updated_at
FROM MYCART
WHERE user_id = #{userId}
  AND item_id = #{itemId}

mapper.update:
UPDATE MYCART
SET quantity = #{quantity},
    updated_at = CURRENT_TIMESTAMP
WHERE user_id = #{userId}
  AND item_id = #{itemId}

mapper.delete:
DELETE FROM MYCART
WHERE user_id = #{userId}
  AND item_id = #{itemId}

mapper.select:
SELECT AMOUNT
FROM ITEMS
WHERE id = #{itemId}
