mapper.select:
SELECT COUNT(*)
FROM ORDERS
WHERE USER_ID = #{userId}

    	SELECT COUNT(*)
    	FROM ORDERS o
    	JOIN ITEMS i ON o.ITEM_ID = i.ID
    	WHERE i.USER_ID = #{userId}

			AND o.ORDER_STATUS = #{orderStatus}

SELECT * FROM ( SELECT o.ID AS id, o.CREATED_AT AS createdAt, i.TITLE AS title, o.AMOUNT AS amount, o.TOTAL_PRICE AS totalPrice, o.ORDER_STATUS AS orderStatus, ROW_NUMBER() OVER(ORDER BY o.CREATED_AT DESC) AS rnum FROM ORDERS o JOIN ITEMS i ON o.ITEM_ID = i.ID JOIN USERS u ON o.USER_ID = u.ID WHERE i.USER_ID = #{userId}
AND o.ORDER_STATUS = #{orderStatus}
) t WHERE t.rnum BETWEEN ((#{page}-1) * #{perPage}) + 1 AND (#{page} * #{perPage})

mapper.update:
UPDATE ORDERS
   SET ORDER_STATUS = #{orderStatus}
 WHERE ID = #{id}
