mapper.insert:
		INSERT INTO ORDER_ITEMS (
          ID, ORDER_ID, ITEM_ID, QUANTITY, PRICE, ORDER_STATUS, CREATED_AT
      ) VALUES (
          ORDER_ITEMS_SEQ.NEXTVAL, #{orderId}, #{itemId}, #{quantity}, #{price}, #{orderStatus, jdbcType=VARCHAR}, CURRENT_TIMESTAMP
      )

mapper.select:
SELECT * FROM (
    SELECT * FROM ITEMS ORDER BY DBMS_RANDOM.VALUE
) WHERE ROWNUM <= #{limit}
