mapper.update:
UPDATE ITEMS
SET CATEGORY_ID = #{categoryId}
	, TITLE = #{title}
	, DESCRIPTION = #{description}
	, DELIVERY = #{delivery}
	, PRICE = #{price}
	, AMOUNT = #{amount}
	, BANK_ACCOUNT_NUMBER = #{bankAccountNumber}
	, IS_DIRECT_DEAL = #{isDirectDeal}
WHERE id=#{id}

UPDATE ITEMS
SET AMOUNT = AMOUNT + #{amount}
WHERE id = #{id}

	AND AMOUNT >= -#{amount}

UPDATE ITEMS
SET VIEWS = views + 1
WHERE ID = #{id}
