mapper.update:
UPDATE ITEMS
SET CATEGORY_ID = #{categoryId}
	, TITLE = #{title}
	, DESCRIPTION = #{description}
	, DELIVERY = #{delivery}
	, PRICE = #{price}
	, AMOUNT = #{amount}
	, BANK_ACCOUNT_NUMBER = #{bankAccountNumber}
	, IS_DIRECT_DEAL = #{isDirectDeal}
WHERE id=#{id}

UPDATE ITEMS
SET AMOUNT = AMOUNT + #{amount}
WHERE id = #{id}

	AND AMOUNT >= -#{amount}

UPDATE ITEMS
SET VIEWS = views + 1
WHERE ID = #{id}

mapper.select:
SELECT * FROM ( SELECT ROWNUM AS rn, i.id, i.title, i.price, i.created_at, NVL(ord_cnt.order_count, 0) AS purchaseCount, ( SELECT ii.filename FROM item_images ii WHERE ii.item_id = i.id AND ii.sort_order = 1 AND ROWNUM = 1 ) AS filename, (SELECT u.nickname FROM users u WHERE u.id = i.user_id) AS sellerName FROM items i LEFT JOIN ( SELECT item_id, COUNT(*) AS order_count FROM orders WHERE order_status != '취소' GROUP BY item_id ) ord_cnt ON i.id = ord_cnt.item_id WHERE i.CATEGORY_ID = #{categoryId} AND i.SALES_STATUS = 'available' ORDER BY
i.title ASC
purchaseCount DESC, i.created_at DESC
i.created_at DESC
) WHERE rn BETWEEN (#{page} - 1) * #{perPage} + 1 AND #{page} * #{perPage}

SELECT * FROM ( SELECT ROWNUM AS rn, i.id, i.title, i.price, i.created_at, NVL(ord_cnt.order_count, 0) AS purchaseCount, ( SELECT ii.filename FROM item_images ii WHERE ii.item_id = i.id AND ii.sort_order = 1 AND ROWNUM = 1 ) AS filename, (SELECT u.nickname FROM users u WHERE u.id = i.user_id) AS sellerName FROM items i LEFT JOIN ( SELECT item_id, COUNT(*) AS order_count FROM orders WHERE order_status != '취소' GROUP BY item_id ) ord_cnt ON i.id = ord_cnt.item_id JOIN CATEGORIES C ON (i.CATEGORY_ID = C.CATEGORY_ID)
ORDER BY
i.title ASC
purchaseCount DESC, i.created_at DESC
i.created_at DESC
) WHERE rn BETWEEN #{page} * #{perPage} - (#{perPage} - 1) AND #{page} * #{perPage}
