mapper.select:
		SELECT *
  	FROM (
      	SELECT ROWNUM rnum, t.*
      	FROM (
          	SELECT *
          	FROM REVIEWS
          	WHERE COMMENT_TEXT LIKE '%' || #{search} || '%'
          	ORDER BY ID DESC
      	) t
      	WHERE ROWNUM <= #{endRow}
  	)
  	WHERE rnum >= #{startRow}

mapper.update:
		UPDATE REVIEWS
        SET rating_number = #{ratingNumber},
            comment_text = #{commentText}
        WHERE id = #{id}

mapper.select:
SELECT NVL(AVG(rating_number), 0)
FROM reviews
WHERE target_id = #{targetId}
